import React from "react";
import { useQuery } from "react-query";
import axios from "axios";

export const Messages = ({ messages, form }) => {
  const fetchMessage = () => {
    return axios.get("/chat/get");
  };
  const { isLoading, data, refetch } = useQuery("info", fetchMessage, {
    refetchOnMount: true,
  });

  const messageClassName =
    "message" + (messages.user === form.user ? "-green" : "");

  return (
    <div className={`${messageClassName}  my-2 w-25`}>
      <div className="userName mx-3">{messages.user}</div>
      <div className="mx-4 fs-5">{messages.title}</div>
      <div className="text-end mx-4">{messages.date}</div>
    </div>
  );
};
