import React from "react";

export const Messages = ({messages, ...props}) => {

    const userName = props.form.map((user) => user.user)

    const check = () => {
        if(userName === messages.user) {
            return true
        }
    }

    const userMessageClassName = 'message'

    return(
        <div className={`${userMessageClassName} bg-light my-2 w-25`}>
            <p className='text-primary mx-2 my-2'>{messages.user}</p>
            <div className=''>
                {messages.title}
            </div>
            <div className='text-end'>
           {messages.date}
            </div>
        </div>
    )
}
