import React, { useState } from "react";
import {
  Container,
  Row,
  Col,
  InputGroup,
  Form,
  Button,
  Modal,
} from "react-bootstrap";
import { useHttp } from "../hooks/http.hook";

export const MessagePage = () => {
  const { request } = useHttp();

  const [form, setForm] = useState({ title: "", user: "" });
  const [show, setShow] = useState(true);

  const changeHandler = (event) => {
    setForm({ ...form, [event.target.name]: event.target.value });
    console.log(form);
  };

  const addMessageHandler = async () => {
    try {
      const data = request("/chat/new", "POST", { ...form });
      setForm({ title: "" });
    } catch (e) {
      console.log(e);
    }
  };

  return (
    <div className="bg-dark">
      <Modal.Dialog show={show}>
        <Modal.Header>
          <Modal.Title>Enter username</Modal.Title>
        </Modal.Header>

        <Modal.Body>
          <InputGroup className="">
            <Form.Control
              placeholder="Enter message"
              aria-label="title"
              name="user"
              aria-describedby="basic-addon2"
              value={form.user}
              onChange={changeHandler}
            />
          </InputGroup>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="primary" onClick={()=> setShow(false)}>Save changes</Button>
        </Modal.Footer>
      </Modal.Dialog>

      <Container>
        <Row md="9" className="d-flex flex-column-reverse"></Row>
        <Row className="d-flex flex-row position-fixed bottom-0 w-75 mb-1">
          <InputGroup className="">
            <Form.Control
              placeholder="Enter message"
              aria-label="title"
              name="title"
              aria-describedby="basic-addon2"
              value={form.title}
              onChange={changeHandler}
            />
            <Button
              variant="primary"
              className="mx"
              onClick={addMessageHandler}
            >
              Enter
            </Button>
          </InputGroup>
        </Row>
      </Container>
    </div>
  );
};
